// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Contracts
import { AccessControlEvents } from "../data.sol";
import "./has-role.sol";

abstract contract int_grantRole_AccessControl_v1 is
    int_hasRole_AccessControl_v1
{
    /**
     * @dev Should only use when circumventing admin checking. See {../entry/grant-role.sol}
     */
    function _grantRole(bytes32 role, address account) internal {
        if (_hasRole(role, account)) return;
        accessControlRolesStore().roles[role].members[account] = true;
        emit AccessControlEvents.RoleGranted(role, account, msg.sender);
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Contracts
import { AccessControlEvents } from "../data.sol";
import "./has-role.sol";

abstract contract int_revokeRole_AccessControl_v1 is
    int_hasRole_AccessControl_v1
{
    function _revokeRole(bytes32 role, address account) internal {
        if (!_hasRole(role, account)) return;
        accessControlRolesStore().roles[role].members[account] = false;
        emit AccessControlEvents.RoleRevoked(role, account, msg.sender);
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import { AccessControlEvents } from "../data.sol";
import "./has-role.sol";

abstract contract int_renounceRole_AccessControl_v1 is
    int_hasRole_AccessControl_v1
{
    function _renounceRole(bytes32 role, address account) internal {
        require(_hasRole(role, msg.sender), "CAN'T RENOUNCE");
        accessControlRolesStore().roles[role].members[account] = false;
        emit AccessControlEvents.RoleRevoked(role, account, msg.sender);
    }
}

