// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
    @notice Utility library of inline functions on the PlatformSetting struct.

    @author develop@teller.finance
 */
library PlatformSettingsLib {
    /**
        It defines a platform settings. It includes: value, min, and max values.
     */
    struct PlatformSetting {
        uint256 value;
        uint256 min;
        uint256 max;
        bool exists;
    }

    /**
        @notice It creates a new platform setting given a name, min and max values.
        @param value initial value for the setting.
        @param min min value allowed for the setting.
        @param max max value allowed for the setting.
     */
    function initialize(
        PlatformSetting storage self,
        uint256 value,
        uint256 min,
        uint256 max
    ) internal {
        requireNotExists(self);
        require(value >= min, "VALUE_MUST_BE_GTE_MIN_VALUE");
        require(value <= max, "VALUE_MUST_BE_LTE_MAX_VALUE");
        self.value = value;
        self.min = min;
        self.max = max;
        self.exists = true;
    }

    /**
        @notice Checks whether the current platform setting exists or not.
        @dev It throws a require error if the platform setting already exists.
        @param self the current platform setting.
     */
    function requireNotExists(PlatformSetting storage self) internal view {
        require(!self.exists, "PLATFORM_SETTING_ALREADY_EXISTS");
    }

    /**
        @notice Checks whether the current platform setting exists or not.
        @dev It throws a require error if the current platform setting doesn't exist.
        @param self the current platform setting.
     */
    function requireExists(PlatformSetting storage self) internal view {
        require(self.exists, "PLATFORM_SETTING_NOT_EXISTS");
    }

    /**
        @notice It updates a current platform setting.
        @dev It throws a require error if:
            - The new value is equal to the current value.
            - The new value is not lower than the max value.
            - The new value is not greater than the min value
        @param self the current platform setting.
        @param newValue the new value to set in the platform setting.
     */
    function update(PlatformSetting storage self, uint256 newValue)
        internal
        returns (uint256 oldValue)
    {
        requireExists(self);
        require(self.value != newValue, "NEW_VALUE_REQUIRED");
        require(newValue >= self.min, "NEW_VALUE_MUST_BE_GTE_MIN_VALUE");
        require(newValue <= self.max, "NEW_VALUE_MUST_BE_LTE_MAX_VALUE");
        oldValue = self.value;
        self.value = newValue;
    }

    /**
        @notice It removes a current platform setting.
        @param self the current platform setting to remove.
     */
    function remove(PlatformSetting storage self) internal {
        requireExists(self);
        self.value = 0;
        self.min = 0;
        self.max = 0;
        self.exists = false;
    }
}

contracts/interfaces/IDappRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../util/TellerCommon.sol";

/**
    @notice This interface defines the functions to manage the Escrow contracts associated to borrowers and loans.

    @author develop@teller.finance
 */
interface IDappRegistry {
    /**
        @notice It gets a dapp configuration based on its contract address.
        @param dapp dapp address.
        @return the dapp details.
     */
    function dapps(address dapp)
        external
        view
        returns (TellerCommon.Dapp memory);

    /**
        @notice It adds a new dapp to the factory.
        @param dapp address to add in this factory.
        @param unsecured boolean to describe if the dapp is allowed to be used with unsecured loans.
     */
    function addDapp(address dapp, bool unsecured) external;

    /**
        @notice It updates a dapp configuration.
        @param dapp address to add in this factory.
        @param unsecured boolean that describes if the dapp can be used by with an unsecured loan.
     */
    function updateDapp(address dapp, bool unsecured) external;

    /**
        @notice It removes a current dapp from the factory.
        @param dapp address to remove.
     */
    function removeDapp(address dapp) external;

    /**
        @notice Gets all the dapps in the factory.
        @return an array of dapps (addresses).
     */
    function getDapps() external view returns (address[] memory);

    /**
        @notice It initializes this escrow contract factory instance.
     */
    function initialize() external;

    /**
        @notice This event is emitted when a new Escrow contract is created.
        @param borrower address associated to the new escrow.
        @param loansAddress loan manager contract address.
        @param loanID loan id associated to the borrower and escrow contract.
        @param escrowAddress the new escrow contract address.
     */
    event EscrowCreated(
        address indexed borrower,
        address indexed loansAddress,
        uint256 indexed loanID,
        address escrowAddress
    );

    /**
        @notice This event is emitted when a new dapp is added to the factory.
        @param sender address.
        @param dapp address added to the factory.
        @param unsecured boolean that describes if the dapp can be used by with an unsecured loan.
     */
    event NewDappAdded(
        address indexed sender,
        address indexed dapp,
        bool unsecured
    );

    /**
        @notice This event is emitted when a dapp is updated.
        @param sender address.
        @param dapp address of dapp contract.
        @param unsecured boolean that describes if the dapp can be used by with an unsecured loan.
     */
    event DappUpdated(
        address indexed sender,
        address indexed dapp,
        bool unsecured
    );

    /**
        @notice This event is emitted when a current dapp is removed from the factory.
        @param sender address.
        @param dapp address removed from the factory.
     */
    event DappRemoved(address indexed sender, address indexed dapp);
}

