// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./AddressLib.sol";

/**
    @notice Utility library of inline functions on the address arrays.

    @author develop@teller.finance
 */
library AddressArrayLib {
    using AddressLib for address;

    struct AddressArray {
        address[] array;
        mapping(address => uint256) indices;
    }

    function length(AddressArray storage self) internal view returns (uint256) {
        return self.array.length;
    }

    /**
      @notice It adds an address value to the array.
      @param self current array.
      @param newItem new item to add.
      @return index the item was added to.
    */
    function add(address[] storage self, address newItem)
        internal
        returns (uint256)
    {
        newItem.requireNotEmpty("EMPTY_ADDRESS_NOT_ALLOWED");
        self.push(newItem);
        return self.length - 1;
    }

    function add(AddressArray storage self, address addr)
        internal
        returns (uint256 index)
    {
        addr.requireNotEmpty("EMPTY_ADDRESS_NOT_ALLOWED");
        (bool found, ) = getIndex(self, addr);
        require(!found, "ADDRESS_EXISTS");
        self.array.push(addr);
        index = length(self) - 1;
        self.indices[addr] = index;
    }

    /**
      @notice It removes the value at the given index in an array.
      @param self the current array.
      @param index remove an item in a specific index.
    */
    function removeAt(address[] storage self, uint256 index) internal {
        if (index >= self.length) return;

        if (index != self.length - 1) {
            self[index] = self[self.length - 1];
        }

        self.pop();
    }

    function remove(AddressArray storage self, uint256 index) internal {
        removeAt(self.array, index);
    }

    function remove(AddressArray storage self, address addr) internal {
        (bool found, uint256 index) = getIndex(self, addr);

        if (!found) return;

        removeAt(self.array, index);
    }

    /**
      @notice It gets the index for a given item.
      @param self the current array.
      @param item to get the index.
      @return found true if the item was found. Otherwise it returns false.
      @return indexAt the current index for a given item.
    */
    function getIndex(address[] storage self, address item)
        internal
        view
        returns (bool found, uint256 indexAt)
    {
        for (indexAt = 0; indexAt < self.length; indexAt++) {
            found = self[indexAt] == item;
            if (found) {
                return (found, indexAt);
            }
        }
        return (found, indexAt);
    }

    function getIndex(AddressArray storage self, address addr)
        internal
        view
        returns (bool found, uint256 index)
    {
        if (self.array.length > 0) {
            index = self.indices[addr];
            found = self.array[index] == addr;
        }
    }

    /**
      @notice It removes an address value from the array.
      @param self the current array.
      @param item the item to remove.
    */
    function remove(address[] storage self, address item) internal {
        (bool found, uint256 indexAt) = getIndex(self, item);
        if (!found) return;

        removeAt(self, indexAt);
    }
}

contracts/interfaces/LendingPoolInterfac.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Utils
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

// Interfaces
import "./IMarketRegistry.sol";
import "./ITToken.sol";
import "../providers/uniswap/IUniswapV2Router02.sol";
import "../providers/compound/CErc20Interface.sol";

/**
    @notice This interface defines the functions for a lending pool that holds all of the tokens
    that lenders transfer into the protocol.

    @author develop@teller.finance
 */
interface LendingPoolInterface {
    /**
        @notice It allows users to deposit tokens into the pool.
        @dev the user must call ERC20.approve function previously.
        @param amount of tokens to deposit in the pool.
    */
    function deposit(uint256 amount) external;

    /**
        @notice It allows any tToken holder to burn their tToken tokens and withdraw their tokens.
        @param amount of tokens to withdraw.
        @dev It throws a require error if the contract hasn't enough tokens balance.
        @dev It throws a require error if the holder hasn't enough tToken balance.
     */
    function withdraw(uint256 amount) external;

    /**
        @notice It allows a borrower repaying their loan.
        @dev This function can be called ONLY by the LoanManager contract.
        @dev It requires a ERC20.approve call before calling it.
        @dev It throws a require error if borrower called ERC20.approve function before calling it.
        @param principalAmount amount of tokens towards the principal.
        @param interestAmount amount of tokens towards the interest.
        @param borrower address that is repaying the loan.
     */
    function repay(
        uint256 principalAmount,
        uint256 interestAmount,
        address borrower
    ) external;

    /**
        @notice Once the loan is created, it transfers the amount of tokens to the borrower.
        @param amount of tokens to transfer.
        @param borrower address which will receive the tokens.
        @dev This function only can be invoked by the LoanManager implementation.
        @dev It throws a require error if current ERC20 balance isn't enough to transfer the tokens.
     */
    function createLoan(uint256 amount, address borrower) external;

    /**
        @notice It gets the lending token address.
        @return the ERC20 lending token address.
    */
    function lendingToken() external view returns (ERC20);

    /**
        @notice It initializes the contract state variables.
        @param aMarketRegistry the MarketRegistry contract.
        @param aLendingToken The underlying token that is used for lending.
        @param aTToken the Teller token to link to the lending pool.
        @param settingsAddress Settings contract address.
        @dev It throws a require error if the contract is already initialized.
     */
    function initialize(
        IMarketRegistry aMarketRegistry,
        address aLendingToken,
        address aTToken,
        address settingsAddress
    ) external;

    /**
        @notice It gets the cToken address.
        @return the cToken address.
    */
    function cToken() external view returns (CErc20Interface);

    function compound() external view returns (IComptroller);

    function comp() external view returns (ERC20);

    /**
        @notice It gets the tToken address.
        @return the tToken address.
    */
    function tToken() external view returns (ITToken);

    /**
        @notice It returns the balance of underlying tokens a lender owns with the amount
        of TTokens owned and the current exchange rate.
        @return a lender's balance of the underlying token in the pool.
     */
    function balanceOfUnderlying(address lender) external returns (uint256);

    /**
        @notice Returns the total amount of interest earned by a lender.
        @dev This value includes already claimed + unclaimed interest earned.
        @return total interest earned by lender.
     */
    function getLenderInterestEarned(address lender) external returns (uint256);

    /**
        @notice Returns the amount of claimable interest a lender has earned.
        @return claimable interest value.
     */
    function getClaimableInterestEarned(address lender)
        external
        returns (uint256);

    /**
        @notice Returns the total amount of interest the pool has earned from repaying loans.
        @return total interest earned from loans.
     */
    function totalInterestEarned() external view returns (uint256);

    /**
        @notice It calculates the market state values across all markets.
        @notice It returns values that represent the global state across all markets.
        @return totalSupplied
        @return totalBorrowed
        @return totalRepaid
        @return totalOnLoan
     */
    function getMarketState()
        external
        view
        returns (
            uint256 totalSupplied,
            uint256 totalBorrowed,
            uint256 totalRepaid,
            uint256 totalOnLoan
        );

    /**
        @notice It gets the debt-to-supply (DtS) ratio for a given market, including a new loan amount.
        @notice The formula to calculate DtS ratio (including a new loan amount) is:

            DtS = (SUM(total borrowed) - SUM(total repaid) + NewLoanAmount) / SUM(total supplied)

        @notice The value has 2 decimal places.
            Example:
                100 => 1%
        @param loanAmount a new loan amount to consider in the ratio.
        @return the debt-to-supply ratio value.
     */
    function getDebtRatioFor(uint256 loanAmount)
        external
        view
        returns (uint256);

    /**
        @notice This event is emitted when an user deposits tokens into the pool.
        @param sender address.
        @param amount of tokens.
     */
    event TokenDeposited(
        address indexed sender,
        uint256 amount,
        uint256 tTokenAmount
    );

    /**
        @notice This event is emitted when an user withdraws tokens from the pool.
        @param sender address that withdrew the tokens.
        @param amount of tokens.
     */
    event TokenWithdrawn(
        address indexed sender,
        uint256 amount,
        uint256 tTokenAmount
    );

    /**
        @notice This event is emitted when an borrower repaid a loan.
        @param borrower address.
        @param amount of tokens.
     */
    event TokenRepaid(address indexed borrower, uint256 amount);

    /**
        @notice This event is emitted when an lender withdraws interests.
        @param lender address.
        @param amount of tokens.
     */
    event InterestWithdrawn(address indexed lender, uint256 amount);

    /**
        @notice This event is emitted when the interest validator is updated.
        @param sender account that sends the transaction.
        @param oldInterestValidator the old validator address.
        @param newInterestValidator the new validator address.
     */
    event InterestValidatorUpdated(
        address indexed sender,
        address indexed oldInterestValidator,
        address indexed newInterestValidator
    );
}

contracts/interfaces/loans/ILoanManager..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Commons
import "../../util/TellerCommon.sol";

// Interfaces
import "../SettingsInterface.sol";
import "./ILoanData.sol";

/**
 * @notice This interface defines the functions to work with the Teller loans protocol
 *
 * @author develop@teller.finance
 */
interface ILoanManager {
    /**
     * @notice Deposit collateral for a loan, unless it isn't allowed
     * @param borrower Account address of the borrower
     * @param loanID ID of loan from which collateral was withdrawn
     * @param amount Amount to be deposited as collateral
     */
    function depositCollateral(
        address borrower,
        uint256 loanID,
        uint256 amount
    ) external payable;

    /**
     * @notice Withdraw collateral from a loan, unless this isn't allowed
     * @param amount The amount of collateral token or ether the caller is hoping to withdraw
     * @param loanID The ID of the loan the collateral is for
     */
    function withdrawCollateral(uint256 amount, uint256 loanID) external;

    /**
     * @notice Create a loan with specified terms, if allowed
     * @param request Struct of the protocol loan request
     * @param responses List of structs of the protocol loan responses
     * @param collateralAmount Amount of collateral for the loan
     */
    function createLoanWithTerms(
        TellerCommon.LoanRequest calldata request,
        TellerCommon.LoanResponse[] calldata responses,
        uint256 collateralAmount
    ) external payable;

    /**
     * @notice Take out a loan, if allowed
     * @param loanID The ID of the loan to be taken out
     * @param amountBorrow Amount of tokens to be taken out in the loan
     */
    function takeOutLoan(uint256 loanID, uint256 amountBorrow) external;

    /**
     * @notice Make a payment to a specified loan
     * @param amount The amount of tokens to pay back to the loan
     * @param loanID The ID of the loan the payment is for
     */
    function repay(uint256 amount, uint256 loanID) external;

    /**
     * @notice Liquidate a loan if has is expired or under collateralized
     * @param loanID The ID of the loan to be liquidated
     */
    function liquidateLoan(uint256 loanID) external;

    /**
        @notice It adds a new account as a signer.
        @param account address to add.
        @dev The sender must be the owner.
        @dev It throws a require error if the sender is not the owner.
     */
    function addSigner(address account) external;

    /**
        @notice It adds a list of account as signers.
        @param accounts addresses to add.
        @dev The sender must be the owner.
        @dev It throws a require error if the sender is not the owner.
     */
    function addSigners(address[] calldata accounts) external;

    /**
     *  @notice It calls the LogicVersionRegistry to update the stored logic address for LoanData.
     */
    function updateLoanDataLogic() external;

    /**
     *  @notice It calls the LogicVersionRegistry to update the stored logic address for LoanTermsConsensus.
     */
    function updateLoanTermsConsensusLogic() external;

    /**
     * @notice Initializes the current contract instance setting the required parameters, if allowed
     * @param lendingPoolAddress Contract address of the lending pool
     * @param settingsAddress Contract address for the configuration of the platform
     * @param collateralTokenAddress Contract address for the collateral token
     * @param initDynamicProxyLogicAddress Address of a deployed InitializableDynamicProxy contract.
     */
    function initialize(
        address lendingPoolAddress,
        address settingsAddress,
        address collateralTokenAddress,
        address initDynamicProxyLogicAddress
    ) external;

    /**
     * @notice This event is emitted when collateral has been deposited for the loan
     * @param loanID ID of the loan for which collateral was deposited
     * @param borrower Account address of the borrower
     * @param depositAmount Amount of collateral deposited
     */
    event CollateralDeposited(
        uint256 indexed loanID,
        address indexed borrower,
        uint256 depositAmount
    );

    /**
     * @notice This event is emitted when collateral has been withdrawn
     * @param loanID ID of loan from which collateral was withdrawn
     * @param borrower Account address of the borrower
     * @param recipient Account address of the recipient
     * @param amount Value of collateral withdrawn
     */
    event CollateralWithdrawn(
        uint256 indexed loanID,
        address indexed borrower,
        address indexed recipient,
        uint256 amount
    );

    /**
     * @notice This event is emitted when loan terms have been successfully set
     * @param loanID ID of loan from which collateral was withdrawn
     * @param borrower Account address of the borrower
     * @param recipient Account address of the recipient
     */
    event LoanTermsSet(
        uint256 indexed loanID,
        address indexed borrower,
        address indexed recipient,
        uint256 nonce
    );

    /**
     * @notice This event is emitted when a loan has been successfully taken out
     * @param loanID ID of loan from which collateral was withdrawn
     * @param borrower Account address of the borrower
     * @param escrow Escrow address associated to this loan
     * @param amountBorrowed Total amount taken out in the loan
     */
    event LoanTakenOut(
        uint256 indexed loanID,
        address indexed borrower,
        address indexed escrow,
        uint256 amountBorrowed
    );

    /**
     * @notice This event is emitted when a loan has been successfully repaid
     * @param loanID ID of loan from which collateral was withdrawn
     * @param borrower Account address of the borrower
     * @param amountPaid Amount of the loan paid back
     * @param payer Account address of the payer
     * @param totalOwed Total amount of the loan to be repaid
     */
    event LoanRepaid(
        uint256 indexed loanID,
        address indexed borrower,
        uint256 amountPaid,
        address payer,
        uint256 totalOwed
    );

    /**
     * @notice This event is emitted when a loan has been successfully liquidated
     * @param loanID ID of loan from which collateral was withdrawn
     * @param borrower Account address of the borrower
     * @param liquidator Account address of the liquidator
     * @param collateralOut Collateral that is sent to the liquidator
     * @param tokensIn Percentage of the collateral price paid by the liquidator to the lending pool
     */
    event LoanLiquidated(
        uint256 indexed loanID,
        address indexed borrower,
        address liquidator,
        int256 collateralOut,
        uint256 tokensIn
    );
}

